    private void jButton7MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton7MouseClicked
        // TODO add your handling code here:
        
        
        File f1 = new File(username+".txt");
            try
            {
                FileOutputStream f = new FileOutputStream(f1);
                f.close();
                f1.delete();
            }
            catch(Exception e)
            {
                JOptionPane.showMessageDialog(null,"Error deleting Account","ERROR",JOptionPane.ERROR_MESSAGE);
            }
            
            
            f1.delete();
            File f2=new File(username+"_profile_data.txt");
            f2.delete();
            setVisible(false);
            //Delete the Account
            
            HelloWorld main = new HelloWorld();
            //main.setSize(getSize());
            main.setLocationRelativeTo(null);
            main.setVisible(true);
            this.dispose();
        
        
    }//GEN-LAST:event_jButton7MouseClicked

